import { useEffect, useState } from "react";
import BookModel from "../../models/BookModel";
import ReviewModel from "../../models/ReviewModel";
import { SpinnerLoading } from "../Utils/SpinnerLoading";
import { StartsReview } from "../Utils/StartsReview";
import { CheckoutAndReviewBox } from "./CheckoutAndReviewBox";
import { LatestReviews } from "./LatestReviews";
import ReviewRequestModel from "../../models/ReviewRequestModel";
import { useAuth0 } from "@auth0/auth0-react";

export const BookCheckoutPage = () => {
  const { isAuthenticated, getAccessTokenSilently } = useAuth0();

  const [book, setBook] = useState<BookModel>();
  const [isLoading, setIsLoading] = useState(true);
  const [httpError, setHttpError] = useState<string | null>(null);

  const [reviews, setReviews] = useState<ReviewModel[]>([]);
  const [totalStars, setTotalStars] = useState(0);
  const [isLoadingReview, setIsLoadingReview] = useState(true);

  const [isReviewLeft, setIsReviewLeft] = useState(false);
  const [isLoadingUserReview, setIsLoadingUserReview] = useState(true);

  const [currentLoansCount, setCurrentLoansCount] = useState(0);
  const [isLoadingCurrentLoansCount, setIsLoadingCurrentLoansCount] =
    useState(true);

  const [isCheckedOut, setIsCheckedOut] = useState(false);
  const [isLoadingBookCheckedOut, setIsLoadingBookCheckedOut] = useState(true);

  const bookId = window.location.pathname.split("/")[2];

  useEffect(() => {
    const fetchBook = async () => {
      const baseUrl = `http://localhost:8080/api/books/${bookId}`;
      const response = await fetch(baseUrl);

      if (!response.ok) {
        throw new Error("Something went wrong!");
      }

      const responseJson = await response.json();

      const loadedBook: BookModel = {
        id: responseJson.id,
        title: responseJson.title,
        author: responseJson.author,
        description: responseJson.description,
        copies: responseJson.copies,
        copiesAvailable: responseJson.copiesAvailable,
        category: responseJson.category,
        img: responseJson.img,
      };

      setBook(loadedBook);
      setIsLoading(false);
    };

    fetchBook().catch((error: any) => {
      setIsLoading(false);
      setHttpError(error.message);
    });
  }, [isCheckedOut]);

  useEffect(() => {
    const fetchBookReviews = async () => {
      const reviewUrl = `http://localhost:8080/api/reviews/search/findByBookId?bookId=${bookId}`;
      const responseReviews = await fetch(reviewUrl);

      if (!responseReviews.ok) {
        throw new Error("Something went wrong!");
      }

      const responseJsonReviews = await responseReviews.json();
      const responseData = responseJsonReviews._embedded.reviews;

      const loadedReviews: ReviewModel[] = [];
      let weightedStarReviews = 0;

      for (const key in responseData) {
        loadedReviews.push({
          id: responseData[key].id,
          userEmail: responseData[key].userEmail,
          date: responseData[key].date,
          rating: responseData[key].rating,
          book_id: responseData[key].bookId,
          reviewDescription: responseData[key].reviewDescription,
        });
        weightedStarReviews += responseData[key].rating;
      }

      if (loadedReviews.length > 0) {
        const round = (
          Math.round((weightedStarReviews / loadedReviews.length) * 2) / 2
        ).toFixed(1);
        setTotalStars(Number(round));
      }

      setReviews(loadedReviews);
      setIsLoadingReview(false);
    };

    fetchBookReviews().catch((error: any) => {
      setIsLoadingReview(false);
      setHttpError(error.message);
    });
  }, [isReviewLeft]);

  useEffect(() => {
    const fetchUserReviewBook = async () => {
      if (isAuthenticated) {
        try {
          const token = await getAccessTokenSilently();
          const url = `http://localhost:8080/api/reviews/secure/user/book/?bookId=${bookId}`;
          const requestOptions = {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
          };

          const userReview = await fetch(url, requestOptions);
          if (!userReview.ok) throw new Error("Something went wrong");
          const userReviewResponseJson = await userReview.json();
          setIsReviewLeft(userReviewResponseJson);
        } catch (error: any) {
          setHttpError(error.message);
        }
        setIsLoadingUserReview(false);
      }
    };

    fetchUserReviewBook();
  }, [isAuthenticated, getAccessTokenSilently]);

  useEffect(() => {
    const fetchUserCurrentLoansCount = async () => {
      if (isAuthenticated) {
        try {
          const token = await getAccessTokenSilently();
          const url = `http://localhost:8080/api/books/secure/currentloans/count`;
          const requestOptions = {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
          };

          const currentLoansCountResponse = await fetch(url, requestOptions);
          if (!currentLoansCountResponse.ok)
            throw new Error("Something went wrong!");
          const currentLoansCountResponseJson =
            await currentLoansCountResponse.json();
          setCurrentLoansCount(currentLoansCountResponseJson);
        } catch (error: any) {
          setHttpError(error.message);
        }
        setIsLoadingCurrentLoansCount(false);
      }
    };

    fetchUserCurrentLoansCount();
  }, [isAuthenticated, getAccessTokenSilently, isCheckedOut]);

  useEffect(() => {
    const fetchUserCheckedOutBook = async () => {
      if (isAuthenticated) {
        try {
          const token = await getAccessTokenSilently();
          const url = `http://localhost:8080/api/books/secure/ischeckedout/byuser/?bookId=${bookId}`;
          const requestOptions = {
            method: "GET",
            headers: {
              Authorization: `Bearer ${token}`,
              "Content-Type": "application/json",
            },
          };

          const bookCheckedOut = await fetch(url, requestOptions);
          if (!bookCheckedOut.ok) throw new Error("Something went wrong!");
          const bookCheckedOutResponseJson = await bookCheckedOut.json();
          setIsCheckedOut(bookCheckedOutResponseJson);
        } catch (error: any) {
          setHttpError(error.message);
        }
        setIsLoadingBookCheckedOut(false);
      }
    };

    fetchUserCheckedOutBook();
  }, [isAuthenticated, getAccessTokenSilently]);

  if (
    isLoading ||
    isLoadingReview ||
    isLoadingCurrentLoansCount ||
    isLoadingBookCheckedOut ||
    isLoadingUserReview
  ) {
    return <SpinnerLoading />;
  }

  if (httpError) {
    return (
      <div className="container m-5">
        <p>{httpError}</p>
      </div>
    );
  }

  async function checkoutBook() {
    try {
      const token = await getAccessTokenSilently();
      const url = `http://localhost:8080/api/books/secure/checkout/?bookId=${book?.id}`;
      const requestOptions = {
        method: "PUT",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      };

      const checkoutResponse = await fetch(url, requestOptions);
      if (!checkoutResponse.ok) throw new Error("Something went wrong!");
      setIsCheckedOut(true);
    } catch (error: any) {
      setHttpError(error.message);
    }
  }

  async function submitReview(starInput: number, reviewDescription: string) {
    try {
      const token = await getAccessTokenSilently();
      const bookIdVal = book?.id || 0;
      const reviewRequestModel = new ReviewRequestModel(
        starInput,
        bookIdVal,
        reviewDescription
      );

      const url = `http://localhost:8080/api/reviews/secure`;
      const requestOptions = {
        method: "POST",
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
        body: JSON.stringify(reviewRequestModel),
      };

      const returnResponse = await fetch(url, requestOptions);
      if (!returnResponse.ok) throw new Error("Something went wrong!");
      setIsReviewLeft(true);
    } catch (error: any) {
      setHttpError(error.message);
    }
  }

  return (
    <div>
      <div className="container d-none d-lg-block">
        <div className="row mt-5">
          <div className="col-sm-2 col-md-2">
            {book?.img ? (
              <img src={book.img} width="226" height="349" alt="Book" />
            ) : (
              <img
                src={require("./../../Images/BooksImages/book-luv2code-1000.png")}
                width="226"
                height="349"
                alt="Book"
              />
            )}
          </div>
          <div className="col-4 col-md-4 container">
            <div className="ml-2">
              <h2>{book?.title}</h2>
              <h5 className="text-primary">{book?.author}</h5>
              <p className="lead">{book?.description}</p>
              <StartsReview rating={totalStars} size={32} />
            </div>
          </div>
          <CheckoutAndReviewBox
            book={book}
            mobile={false}
            currentLoansCount={currentLoansCount}
            isAuthenticated={isAuthenticated}
            isCheckedOut={isCheckedOut}
            checkoutBook={checkoutBook}
            isReviewLeft={isReviewLeft}
            submitReview={submitReview}
          />
        </div>
        <hr />
        <LatestReviews reviews={reviews} bookId={book?.id} mobile={false} />
      </div>

      <div className="container d-lg-none mt-5">
        <div className="d-flex justify-content-center align-items-center">
          {book?.img ? (
            <img src={book.img} width="226" height="349" alt="Book" />
          ) : (
            <img
              src={require("./../../Images/BooksImages/book-luv2code-1000.png")}
              width="226"
              height="349"
              alt="Book"
            />
          )}
        </div>
        <div className="mt-4">
          <div className="ml-2">
            <h2>{book?.title}</h2>
            <h5 className="text-primary">{book?.author}</h5>
            <p className="lead">{book?.description}</p>
            <StartsReview rating={totalStars} size={32} />
          </div>
        </div>
        <CheckoutAndReviewBox
          book={book}
          mobile={true}
          currentLoansCount={currentLoansCount}
          isAuthenticated={isAuthenticated}
          isCheckedOut={isCheckedOut}
          checkoutBook={checkoutBook}
          isReviewLeft={isReviewLeft}
          submitReview={submitReview}
        />
        <hr />
        <LatestReviews reviews={reviews} bookId={book?.id} mobile={true} />
      </div>
    </div>
  );
};
